styles:
.jui-badge {
    position: relative;
    box-sizing: border-box;
    padding: 0;
    margin: 0;
    display: inline-block;
}
:host /deep/ .jui-badge>div {
    display: inline-block;
}
.jui-badge .display-text {
    display: inline-block;
    padding: 0 6px;
    border-radius: 10px;
    height: 20px;
    text-align: center;
    line-height: 20px;
    min-width: 20px;
    position:absolute;
    top: -10px;
    left: 100%;
    -webkit-transform: translateX(-50%);
    -ms-transform: translateX(-50%);
    transform: translateX(-50%);
    -webkit-transform-origin: -10% center;
    -ms-transform-origin: -10% center;
    transform-origin: -10% center;
}
.jui-badge .display-dot {
    display: inline-block;
    border-radius: 50%;
    height: 8px;
    width: 8px;
    position:absolute;
    top: -4px;
    left: 100%;
    -webkit-transform: translateX(-50%);
    -ms-transform: translateX(-50%);
    transform: translateX(-50%);
    -webkit-transform-origin: -10% center;
    -ms-transform-origin: -10% center;
    transform-origin: -10% center;
}
.jui-badge .display-no-wrapper {
    display: inline-block;
    position: inherit;
    padding: 0 6px;
    border-radius: 10px;
    height: 20px;
    text-align: center;
    line-height: 20px;
    min-width: 20px;
    background-color: #f04134;
    color: white;
}
